/**
 * Creates [RootScreen] with provided screen, if there is no saved value.
 * Otherwise
 * Integration point for your screen hierarchy. You can use this fun to integrate Modo to your Fragment or Activity.
 */
@Composable
fun <T : Screen> rememberRootScreen(
    rootScreenFactory: () -> T
): RootScreen<T> {
    val rootScreen = rememberSaveable {
        RootScreen(rootScreenFactory())
    }
    val context = LocalContext.current
    val activity by remember {
        derivedStateOf {
            context.getActivity()
        }
    }
    DisposableEffect(rootScreen, activity) {
        onDispose {
            if (activity?.isFinishing == true) {
                onRootScreenFinished(rootScreen)
            }
        }
    }
    return rootScreen
}

